    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String nombre = JOptionPane.showInputDialog("Ingrese nombre del local");
        nombre = nombre.toUpperCase();
        String comuna = JOptionPane.showInputDialog("Ingrese comuna del local");
        comuna = comuna.toUpperCase();
        String direccion = JOptionPane.showInputDialog("Ingrese direccion del local");
        direccion = direccion.toUpperCase();
        String capacidadStr = JOptionPane.showInputDialog("Ingrese capacidad maxima del local");
        int capacidadMaxima = Integer.parseInt(capacidadStr);

        while(capacidadMaxima < 100){
            capacidadStr = JOptionPane.showInputDialog("Ingrese un valor mayor o igual a 100");
            capacidadMaxima = Integer.parseInt(capacidadStr);
        }
        String numeroMesaStr = JOptionPane.showInputDialog("Ingrese numero de la primera mesa del local");
        int numeroPrimeraMesa = Integer.parseInt(numeroMesaStr);
        while(numeroPrimeraMesa <= 0){
            numeroMesaStr = JOptionPane.showInputDialog("Ingrese un valor mayor a 0");
            numeroPrimeraMesa = Integer.parseInt(capacidadStr);
        }
        String numeroUltimaMesaStr = JOptionPane.showInputDialog("Ingrese numero de la ultima mesa del local");
        int numeroUltimaMesa = Integer.parseInt(numeroUltimaMesaStr);
        while(numeroUltimaMesa <= numeroPrimeraMesa){
            numeroMesaStr = JOptionPane.showInputDialog("Ingrese un valor mayor a ",+numeroPrimeraMesa);
            numeroUltimaMesa = Integer.parseInt(numeroMesaStr);
        }
        
        
        Local a = new Local(nombre, comuna, direccion, capacidadMaxima, numeroPrimeraMesa, numeroUltimaMesa);
        
        if( valparaiso.agregarLocal(a) == true){
            JOptionPane.showMessageDialog(rootPane, "Se agrego local con exito");
        }else JOptionPane.showMessageDialog(rootPane, "No se agrego local");
       
    }//GEN-LAST:event_jButton1ActionPerformed

